mapper.select:
select count(*) as total
from check_list
where doc_no = #{doc_no}

select count(*) as total
from check_list
where facility_code = #{facility_code}

select count(*) as total
from check_report
where facility_code = #{facility_code}

select count(*) as total from ( select row_number() over() as rn,ui.user_name, cr.*, wr.facility_category, org.org_name, ar.org_area_name,ui.user_department from check_report cr join water_resources wr on cr.facility_code = wr.facility_code INNER JOIN org_area ar ON wr.org_area =ar.org_area INNER JOIN ORGANIZATION org ON wr.org_code =org.org_code INNER JOIN user_info ui ON cr.user_id =ui.user_id where wr.facility_category like '%' || #{facility_category} || '%' and ar.org_area like '%' || #{org_area} || '%' and org.org_name like '%' || #{org_name} || '%' and ui.user_department like '%' || #{user_department} || '%' and cr.facility_code like '%' || #{facility_code} || '%'
order by cr.modify_datetime
and cr.modify_datetime <= #{secdate}
and cr.modify_datetime >= #{firstdate}
and cr.modify_datetime between #{firstdate} and #{secdate}
)

select *
from user_info
where user_name = #{user_name}
  and user_position = #{user_position}
  and user_department = #{user_department}

select count(*) as total from ( select wr.facility_category,wr.facility_code,wr.facility_addr,oz.org_area_name, cr.firstdate,cr.check_result,cr.user_id, org.org_name from water_resources wr left outer join (select distinct to_char(max(modify_datetime),'yyyy-MM-dd') as firstdate, facility_code ,check_result ,user_id from check_report group by facility_code,check_result,user_id)cr on wr.facility_code = cr.facility_code left join org_area oz on wr.org_area = oz.org_area left join organization org on wr.org_area = org.org_area
and cr.firstdate <= #{secdate}
and cr.firstdate >= #{firstdate}
and cr.firstdate between #{firstdate} and #{secdate}
) as tab left join user_info ui on tab.user_id = ui.user_id where tab.facility_category like '%' || #{facility_category} || '%' and tab.org_area_name like '%' || #{org_area} || '%' and tab.org_name like '%' || #{org_name} || '%'
and ui.user_department like '%' || #{user_department} || '%'
and tab.facility_code like '%' || #{facility_code} || '%'

select ROW_NUMBER() over() as rn, tab.facility_category,tab.facility_code,tab.facility_addr,tab.org_area_name, tab.firstdate,cc.check_result,ui.user_name, tab.org_name,ui.user_department from ( select wr.facility_category,wr.facility_code,wr.facility_addr,oz.org_area_name, cr.firstdate, org.org_name, (select max(doc_no) from check_report where facility_code = wr.facility_code) as doc_no from water_resources wr left outer join (select distinct to_char(max(modify_datetime),'yyyy-MM-dd') as firstdate, facility_code from check_report group by facility_code)cr on wr.facility_code = cr.facility_code
left join org_area oz on wr.org_area = oz.org_area left join organization org on wr.org_area = org.org_area
and cr.firstdate <= #{secdate}
and cr.firstdate >= #{firstdate}
and cr.firstdate between #{firstdate} and #{secdate}
) as tab left join check_report cc on cc.doc_no = tab.doc_no left join user_info ui on cc.user_id = ui.user_id where tab.facility_category like '%' || #{facility_category} || '%' and tab.org_area_name like '%' || #{org_area} || '%' and tab.org_name like '%' || #{org_name} || '%'
and ui.user_department like '%' || #{user_department} || '%'
and tab.facility_code like '%' || #{facility_code} || '%'
offset ${start}-1 limit 10

select * from (select ROW_NUMBER() over() as rn, wr.*,cd.cate_name, (select org_name from organization where org_area like '%' || #{org_area} || '%'),split_part(wr.facility_addr::varchar, ' ', 1) as first_area, split_part(wr.facility_addr::varchar, ' ', 2)as second_area from water_resources wr, organization org, code cd where wr.org_code = org.org_code and wr.facility_type = cd.cate_code
and wr.facility_category = #{facility_category}
and wr.org_area like '%' || #{org_area} || '%'
and wr.facility_code like '%' || #{facility_code} || '%'
and cd.cate_name like '%' || #{cate_name} || '%'
order by rn) where rn between #{start} and #{end}

select count(*) as total from (select ROW_NUMBER() over() as rn, wr.*,cd.cate_name, (select org_name from organization where org_area like '%' || #{org_area} || '%'),split_part(wr.facility_addr::varchar, ' ', 1) as first_area, split_part(wr.facility_addr::varchar, ' ', 2)as second_area from water_resources wr, organization org, code cd where wr.org_code = org.org_code and wr.facility_type = cd.cate_code
and wr.facility_category = #{facility_category}
and wr.org_area like '%' || #{org_area} || '%'
and wr.facility_code like '%' || #{facility_code} || '%'
and cd.cate_name like '%' || #{cate_name} || '%'
)

select *
from water_resources wr,
     code cd
where wr.facility_type = cd.cate_code
  and wr.facility_code = #{facility_code}
